params.query:
-- Create matches table
CREATE TABLE IF NOT EXISTS matches (
  id UUID PRIMARY KEY DEFAULT gen_random_uuid(),
  player1_id UUID REFERENCES user_profiles(id) ON DELETE CASCADE,
  player2_id UUID REFERENCES user_profiles(id) ON DELETE CASCADE,
  player1_score INTEGER NOT NULL DEFAULT 0,
  player2_score INTEGER NOT NULL DEFAULT 0,
  winner_id UUID REFERENCES user_profiles(id) ON DELETE SET NULL,
  status TEXT CHECK (status IN ('scheduled', 'completed', 'in-progress')) DEFAULT 'completed',
  week INTEGER DEFAULT 1,
  created_by UUID REFERENCES auth.users(id) ON DELETE CASCADE,
  created_at TIMESTAMP WITH TIME ZONE DEFAULT NOW(),
  updated_at TIMESTAMP WITH TIME ZONE DEFAULT NOW()
);

-- Enable RLS
ALTER TABLE matches ENABLE ROW LEVEL SECURITY;

-- Create RLS policies
CREATE POLICY "Users can view all matches" ON matches FOR SELECT USING (true);
CREATE POLICY "Users can create matches" ON matches FOR INSERT WITH CHECK (created_by = auth.uid());
CREATE POLICY "Users can update their own matches" ON matches FOR UPDATE USING (created_by = auth.uid());
